  `fechaCaducidad` varchar(255),
  `lote` varchar(255),  
  `borrado` bool default false, 
  primary key (`idProducto`)
);

DROP TABLE `t_detalleCompra`;

CREATE TABLE `t_detalleCompra` (  
  `idCompra` mediumint default NULL,
  `idProducto` mediumint default NULL,
  `cantidad` mediumint default NULL,
  `total` varchar(255) default NULL,
  primary key (`idCompra`, `idProducto`)
) AUTO_INCREMENT=1;

