SELECT t_cliente.nombreCompania, t_cliente.idCliente, t_ventas.idVenta,  (select count(*) from t_ventas where t_ventas.idCliente = t_cliente.idCliente) as cuenta
FROM `t_ventas`
INNER JOIN `t_cliente` on t_cliente.idCliente = t_ventas.idCliente GROUP BY idCliente; 

#Creación de vista para clientes con mayores
drop view v_ciudadesMayoresCompras;
drop view v_clientesFrecuentes;
drop view v_clientesMayoresCompras;
drop view v_clientesporregion;

CREATE VIEW v_clientesFrecuentes AS 
(
SELECT 
t_cliente.idCliente, 
t_cliente.nombreCompania, 
t_cliente.ciudad, 
(select count(*) from t_ventas where t_ventas.idCliente = t_cliente.idCliente) as comprasRealizadas
FROM `t_ventas`
INNER JOIN `t_cliente` on t_cliente.idCliente = t_ventas.idCliente GROUP BY t_cliente.idCliente order by comprasRealizadas desc
);

  CREATE VIEW v_clientesMayoresCompras AS 
  (
  SELECT 
  t_cliente.idCliente, 
  t_cliente.nombreCompania, 
  t_cliente.ciudad, 
  #(select count(*) from t_ventas where t_ventas.idCliente = t_cliente.idCliente) as 'Compras realizadas',
  (select SUM(monto) from t_ventas where idCliente=t_cliente.idCliente ) as montoTotal
  FROM `t_ventas`
  INNER JOIN `t_cliente` on t_cliente.idCliente = t_ventas.idCliente GROUP BY t_cliente.idCliente order by montoTotal desc
  );

CREATE VIEW v_ciudadesMayoresCompras AS 
(
SELECT t_cliente.idCliente, 
t_cliente.nombreCompania, 
t_cliente.ciudad, 
(select count(*) from t_ventas where t_ventas.ciudadDestinatario = t_cliente.ciudad) as Compras_por_ciudad,
(select SUM(monto) from t_ventas where t_ventas.ciudadDestinatario =t_cliente.ciudad ) as montoTotal
FROM `t_ventas`
INNER JOIN `t_cliente` on t_cliente.idCliente = t_ventas.idCliente GROUP BY t_cliente.ciudad order by Compras_por_ciudad desc
);


CREATE VIEW v_clientesPorCiudad AS 
(
select ciudad, 
(select count(*) from t_cliente where t_cliente.ciudad = v_ciudadesMayoresCompras.ciudad) as cantidadDeClientes
from v_ciudadesMayoresCompras
);


CREATE VIEW v_empleadosSexo AS 
(
	SELECT t_empleados.sexo as sexo,
	(select count(t_empleados.sexo=t_empleados.sexo)) as cantidad
	FROM `t_empleados` GROUP BY sexo 
);

create view v_empleadosCargo as (
SELECT t_empleados.cargo as cargo,
	(select count(t_empleados.cargo=t_empleados.cargo)) as cantidad
	FROM `t_empleados` GROUP BY cargo 
    ); 
create view v_clientesCiudad as (
SELECT t_cliente.ciudad, 
	(select count(t_cliente.ciudad=t_cliente.ciudad)) as cantidad
	FROM `t_cliente` GROUP BY ciudad
    );
    
create view v_clientesCargo as (
SELECT t_cliente.cargoContacto, 
	(select count(t_cliente.cargoContacto=t_cliente.cargoContacto)) as cantidad
	FROM `t_cliente` GROUP BY cargoContacto
    );